  render: () => <div className="grid gap-4" style={{
    '--bs-columns': 3,
    width: 800
  }}>
      <DCard className="w-100" style={{
      maxWidth: '350px'
    }}>
        <DCard.Body className="text-center">
          <DIcon icon="ShieldCheck" size="3rem" className="mb-3 text-primary" />
          <h5 className="card-title">Basic Plan</h5>
          <p className="h2 my-4">$49/mo</p>
          <ul className="list-unstyled text-start mb-4">
            <li className="mb-2">
              <DIcon icon="CheckCircle" className="text-success me-2" />
              $100,000 Coverage
            </li>
            <li className="mb-2">
              <DIcon icon="CheckCircle" className="text-success me-2" />
              24/7 Support
            </li>
            <li className="mb-2 text-muted">
              <DIcon icon="XCircle" className="me-2" />
              Roadside Assistance
            </li>
          </ul>
          <DButton color="primary" className="w-100" text="Choose Plan" />
        </DCard.Body>
      </DCard>
      <DCard className="w-100 bg-primary text-white" style={{
      maxWidth: '350px'
    }}>
        <DCard.Body className="text-center">
          <DIcon icon="Shield" size="3rem" className="mb-3 text-white" />
          <h5 className="card-title mb-0">Premium Plan</h5>
          <p className="h2 my-4">$99/mo</p>
          <ul className="list-unstyled text-start mb-4">
            <li className="mb-2">
              <DIcon icon="CheckCircle" className="text-white me-2" />
              $500,000 Coverage
            </li>
            <li className="mb-2">
              <DIcon icon="CheckCircle" className="text-white me-2" />
              24/7 Support
            </li>
            <li className="mb-2">
              <DIcon icon="CheckCircle" className="text-white me-2" />
              Roadside Assistance
            </li>
          </ul>
          <DButton color="light" className="w-100" text="Choose Plan" />
        </DCard.Body>
      </DCard>

      <DCard className="w-100 text-white" style={{
      maxWidth: '350px',
      background: 'linear-gradient(to right, #cc2b5e, #753a88)'
    }}>
        <DCard.Body className="text-center">
          <DIcon icon="Shield" size="3rem" className="mb-3 text-white" />
          <h5 className="card-title mb-0">Premium Plan</h5>
          <p className="h2 my-4">$99/mo</p>
          <ul className="list-unstyled text-start mb-4">
            <li className="mb-2">
              <DIcon icon="CheckCircle" className="text-white me-2" />
              $500,000 Coverage
            </li>
            <li className="mb-2">
              <DIcon icon="CheckCircle" className="text-white me-2" />
              24/7 Support
            </li>
            <li className="mb-2">
              <DIcon icon="CheckCircle" className="text-white me-2" />
              Roadside Assistance
            </li>
          </ul>
          <DButton color="light" className="w-100" text="Choose Plan" />
        </DCard.Body>
      </DCard>
    </div>
